import api from './api';

const teacherApi = {
    dashboard: {
        // Dashboard Overview
        getOverview: async () => {
            try {
                const response = await api.get('/api/dashboard/teacher/');
                return response.data;
            } catch (error) {
                console.error('Error fetching teacher dashboard:', error);
                throw error;
            }
        },

        // Get All Students
        getStudents: async () => {
            try {
                const response = await api.get('/api/dashboard/teacher/students/');
                return response.data;
            } catch (error) {
                console.error('Error fetching students:', error);
                throw error;
            }
        },

        // Get All Mentor Evaluations
        getMentorEvaluations: async () => {
            try {
                const response = await api.get('/api/dashboard/teacher/evaluations/');
                return response.data;
            } catch (error) {
                console.error('Error fetching mentor evaluations:', error);
                throw error;
            }
        }
    },

    evaluations: {
        // Get All Evaluations
        getAll: async () => {
            try {
                const response = await api.get('/api/evaluations/teacher/all/');
                return response.data;
            } catch (error) {
                console.error('Error fetching evaluations:', error);
                throw error;
            }
        },

        // Get Mentor Evaluations
        getMentorEvaluations: async () => {
            try {
                const response = await api.get('/api/evaluations/teacher/mentor/all/');
                return response.data;
            } catch (error) {
                console.error('Error fetching mentor evaluations:', error);
                throw error;
            }
        },

        // Review Evaluation
        reviewEvaluation: async (evaluationId, data) => {
            try {
                const response = await api.post(`/api/evaluations/teacher/review/${evaluationId}/`, data);
                return response.data;
            } catch (error) {
                console.error('Error reviewing evaluation:', error);
                throw error;
            }
        }
    },

    students: {
        // Get All Students
        getAll: async () => {
            try {
                const response = await api.get('/api/students/teacher/');
                return response.data;
            } catch (error) {
                console.error('Error fetching students:', error);
                throw error;
            }
        },

        // Get Student Details
        getDetails: async (studentId) => {
            try {
                const response = await api.get(`/api/students/teacher/${studentId}/`);
                return response.data;
            } catch (error) {
                console.error('Error fetching student details:', error);
                throw error;
            }
        },

        // Monitor Student Progress
        getProgress: async (studentId) => {
            try {
                const response = await api.get(`/api/students/teacher/${studentId}/progress/`);
                return response.data;
            } catch (error) {
                console.error('Error fetching student progress:', error);
                throw error;
            }
        }
    },

    reports: {
        // Get All Reports
        getAll: async () => {
            try {
                const response = await api.get('/api/reports/teacher/');
                return response.data;
            } catch (error) {
                console.error('Error fetching reports:', error);
                throw error;
            }
        },

        // Review Report
        reviewReport: async (reportId, data) => {
            try {
                const response = await api.post(`/api/reports/teacher/review/${reportId}/`, data);
                return response.data;
            } catch (error) {
                console.error('Error reviewing report:', error);
                throw error;
            }
        }
    }
};

export default teacherApi;
