import React from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const ViewEvaluation = () => {
  const { evaluationId } = useParams();
  const navigate = useNavigate();

  // Replace this with actual data fetched from your API
  const evaluation = {
    id: evaluationId,
    title: `Evaluation ${evaluationId}`,
    date: '2024-04-15',
    content: 'Evaluation content goes here...',
    status: 'completed',
  };

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">{evaluation.title}</h2>
        <button
          onClick={() => navigate('/dashboard/evaluations')}
          className="px-4 py-2 border rounded-md text-gray-700 hover:bg-gray-50"
        >
          Back to Evaluations
        </button>
      </div>

      <div className="bg-white rounded-lg shadow p-6">
        <div className="mb-4">
          <span
            className={`px-3 py-1 rounded-full text-sm ${
              evaluation.status === 'completed'
                ? 'bg-green-100 text-green-800'
                : 'bg-yellow-100 text-yellow-800'
            }`}
          >
            {evaluation.status}
          </span>
          <p className="text-sm text-gray-500 mt-2">{evaluation.date}</p>
        </div>
        <div className="prose max-w-none">
          <p>{evaluation.content}</p>
        </div>
      </div>
    </div>
  );
};

export default ViewEvaluation;
