import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getStudentDashboard, getStudentStats } from '../../services/studentApi';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-hot-toast';
import {
  DocumentTextIcon,
  ClipboardDocumentCheckIcon,
  UserGroupIcon,
  CalendarIcon,
  ChartBarIcon,
  UserIcon,
  DocumentCheckIcon,
  ClockIcon,
} from '@heroicons/react/24/outline';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    reportsSubmitted: 0,
    daysRemaining: 0,
    overallProgress: 0,
    recentActivity: []
  });
  const [stats, setStats] = useState({
    reports_submitted: 0,
    days_remaining: 0,
    tasks_completed: 0,
    progress: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [dashboardResponse, statsResponse] = await Promise.all([
          getStudentDashboard(),
          getStudentStats()
        ]);
        setDashboardData(dashboardResponse.data || {
          reportsSubmitted: 0,
          daysRemaining: 0,
          overallProgress: 0,
          recentActivity: []
        });
        setStats(statsResponse.data || {
          reports_submitted: 0,
          days_remaining: 0,
          tasks_completed: 0,
          progress: 0
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  const StatCard = ({ title, value, icon: Icon, color }) => (
    <div className="bg-white overflow-hidden shadow rounded-lg">
      <div className="p-5">
        <div className="flex items-center">
          <div className="flex-shrink-0">
            <Icon className={`h-6 w-6 ${color}`} />
          </div>
          <div className="ml-5 w-0 flex-1">
            <dl>
              <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
              <dd className="text-lg font-semibold text-gray-900">{value}</dd>
            </dl>
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name || 'Student'}
        </h1>
        <p className="mt-1 text-sm text-gray-500">
          Here's an overview of your internship progress
        </p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <StatCard
          title="Reports Submitted"
          value={stats.reports_submitted || 0}
          icon={DocumentTextIcon}
          color="text-blue-600"
        />
        <StatCard
          title="Days Remaining"
          value={stats.days_remaining || 0}
          icon={CalendarIcon}
          color="text-emerald-600"
        />
        <StatCard
          title="Tasks Completed"
          value={stats.tasks_completed || 0}
          icon={ClipboardDocumentCheckIcon}
          color="text-purple-600"
        />
        <StatCard
          title="Overall Progress"
          value={`${stats.progress || 0}%`}
          icon={ChartBarIcon}
          color="text-yellow-600"
        />
      </div>

      {/* Recent Activity */}
      {dashboardData.recentActivity && dashboardData.recentActivity.length > 0 && (
        <div className="mt-8">
          <h2 className="text-lg font-medium text-gray-900">Recent Activity</h2>
          <div className="mt-4 bg-white shadow overflow-hidden sm:rounded-md">
            <ul role="list" className="divide-y divide-gray-200">
              {dashboardData.recentActivity.map((activity, index) => (
                <li key={index}>
                  <div className="px-4 py-4 sm:px-6">
                    <div className="flex items-center justify-between">
                      <p className="text-sm font-medium text-emerald-600 truncate">
                        {activity.title}
                      </p>
                      <div className="ml-2 flex-shrink-0 flex">
                        <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                          {activity.type}
                        </p>
                      </div>
                    </div>
                    <div className="mt-2 sm:flex sm:justify-between">
                      <div className="sm:flex">
                        <p className="text-sm text-gray-500">
                          {activity.description}
                        </p>
                      </div>
                      <div className="mt-2 flex items-center text-sm text-gray-500 sm:mt-0">
                        <p>
                          {new Date(activity.timestamp).toLocaleDateString()}
                        </p>
                      </div>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentDashboard;
