import axios from 'axios';
import { getToken, removeToken } from '../utils/auth';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:8000',
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000,
});

// Request interceptor
api.interceptors.request.use(
  (config) => {
    const token = getToken();
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

// Response interceptor
api.interceptors.response.use(
  (response) => response,
  async (error) => {
    const originalRequest = error.config;

    if (error.response?.status === 401 && !originalRequest._retry) {
      originalRequest._retry = true;

      if (error.response.data.message === 'Token expired') {
        try {
          const response = await api.post('/api/token/refresh/');
          const { token } = response.data;
          originalRequest.headers.Authorization = `Bearer ${token}`;
          return api(originalRequest);
        } catch (refreshError) {
          removeToken();
          window.location.href = '/login';
          return Promise.reject(refreshError);
        }
      }

      removeToken();
      window.location.href = '/login';
    }

    return Promise.reject(error);
  }
);

// Auth endpoints - Export these directly
export const login = async (credentials) => {
  try {
    const response = await api.post('/api/auth/login/', {
      username: credentials.username,
      password: credentials.password,
    });

    console.log('Raw login response:', response);

    // Handle Django REST framework's typical response format
    const token = response.data.access || response.data.token;
    const refresh = response.data.refresh; // Some APIs provide a refresh token

    if (!token) {
      throw new Error('No authentication token received');
    }

    // Store refresh token if provided
    if (refresh) {
      localStorage.setItem('refresh_token', refresh);
    }

    return {
      data: {
        token: token,
        user: response.data.user || {}
      }
    };
  } catch (error) {
    console.error('Login error details:', {
      status: error.response?.status,
      data: error.response?.data,
      message: error.message
    });
    throw error;
  }
};
export const register = (userData) => api.post('/api/auth/register', userData);
export const logout = () => api.post('/api/auth/logout');
export const getMe = () => api.get('/api/users/me');
export const refreshToken = () => api.post('/api/auth/refresh');
export const forgotPassword = (email) => api.post('/api/auth/forgot-password', { email });
export const resetPassword = (token, password) => 
  api.post('/api/auth/reset-password', { token, password });

// Also export as part of authService object
export const authService = {
  login,
  register,
  logout,
  getMe,
  refreshToken,
  forgotPassword,
  resetPassword,
};

export default api;
