import React from 'react';
import { Link } from 'react-router-dom';
import { Plus, FileText } from 'lucide-react';

const ReportsList = () => {
  // This will be replaced with actual data from your API
  const reports = [
    { id: 1, title: 'Weekly Report 1', date: '2024-03-20', status: 'submitted' },
    { id: 2, title: 'Weekly Report 2', date: '2024-03-27', status: 'draft' },
  ];

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">My Reports</h2>
        <Link
          to="create"
          className="inline-flex items-center px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700"
        >
          <Plus className="h-5 w-5 mr-2" />
          New Report
        </Link>
      </div>

      <div className="bg-white rounded-lg shadow">
        <div className="grid gap-4 p-4">
          {reports.map((report) => (
            <Link
              key={report.id}
              to={`${report.id}`}
              className="flex items-center p-4 border rounded-lg hover:bg-gray-50"
            >
              <FileText className="h-6 w-6 text-emerald-600 mr-3" />
              <div className="flex-1">
                <h3 className="font-medium text-gray-900">{report.title}</h3>
                <p className="text-sm text-gray-500">{report.date}</p>
              </div>
              <span
                className={`px-3 py-1 rounded-full text-sm ${
                  report.status === 'submitted'
                    ? 'bg-green-100 text-green-800'
                    : 'bg-yellow-100 text-yellow-800'
                }`}
              >
                {report.status}
              </span>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ReportsList;
