import React from 'react';
import { Link } from 'react-router-dom';
import clsx from 'clsx';

const variants = {
  primary: 'bg-emerald-600 hover:bg-emerald-700 text-white',
  secondary: 'bg-gray-600 hover:bg-gray-700 text-white',
  outline: 'border border-gray-300 hover:bg-gray-50 text-gray-700',
  danger: 'bg-red-600 hover:bg-red-700 text-white',
  ghost: 'hover:bg-gray-100 text-gray-700',
};

const sizes = {
  sm: 'px-3 py-1.5 text-sm',
  md: 'px-4 py-2 text-base',
  lg: 'px-6 py-3 text-lg',
};

const Button = ({
  children,
  variant = 'primary',
  size = 'md',
  className,
  disabled,
  loading,
  icon: Icon,
  as: Component = 'button',
  href,
  to,
  ...props
}) => {
  // Determine the component to render (button, Link, or a)
  const ButtonComponent = to ? Link : href ? 'a' : Component;

  // Combine all props based on the component type
  const buttonProps = {
    ...(to ? { to } : {}),
    ...(href ? { href } : {}),
    className: clsx(
      'inline-flex items-center justify-center rounded-md font-medium transition-colors focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-emerald-500',
      variants[variant],
      sizes[size],
      disabled && 'opacity-50 cursor-not-allowed',
      className
    ),
    disabled: disabled || loading,
    ...props,
  };

  return (
    <ButtonComponent {...buttonProps}>
      {loading && (
        <svg
          className="animate-spin -ml-1 mr-2 h-4 w-4"
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
        >
          <circle
            className="opacity-25"
            cx="12"
            cy="12"
            r="10"
            stroke="currentColor"
            strokeWidth="4"
          />
          <path
            className="opacity-75"
            fill="currentColor"
            d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z"
          />
        </svg>
      )}
      {Icon && !loading && (
        <Icon className={clsx('h-5 w-5', children && 'mr-2')} />
      )}
      {children}
    </ButtonComponent>
  );
};

export { Button };
export default Button;
