import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-hot-toast';
import studentApi from '../../services/studentApi';

// Import Student Components
import PreliminaryReportCheck from '../../components/student/PreliminaryReportCheck';
import ViewInternshipDuration from '../../components/student/ViewInternshipDuration';
import ViewMentorEvaluation from '../../components/student/ViewMentorEvaluation';
import ViewTeacherEvaluation from '../../components/student/ViewTeacherEvaluation';
import RegisterInternship from '../../components/student/RegisterInternship';
import SubmitReport from '../../components/student/SubmitReport';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const data = await studentApi.dashboard.getOverview();
      setDashboardData(data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  // Check if student has active internship
  const hasActiveInternship = dashboardData?.activeInternship;

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name}!
        </h1>
        <p className="mt-1 text-sm text-gray-500">
          Here's an overview of your internship progress
        </p>
      </div>

      {/* Main Dashboard Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Left Column */}
        <div className="space-y-6">
          {!hasActiveInternship ? (
            // Show Register Internship if no active internship
            <RegisterInternship />
          ) : (
            // Show Internship Duration for active internship
            <ViewInternshipDuration />
          )}

          {/* Preliminary Report Check */}
          <PreliminaryReportCheck />

          {/* Submit Report (only show if has active internship) */}
          {hasActiveInternship && <SubmitReport />}
        </div>

        {/* Right Column */}
        <div className="space-y-6">
          {/* Evaluations */}
          <ViewMentorEvaluation />
          <ViewTeacherEvaluation />
        </div>
      </div>

      {/* Recent Activities Section */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">
          Recent Activities
        </h2>
        <div className="space-y-4">
          {dashboardData?.recentActivities?.map((activity, index) => (
            <div
              key={index}
              className="flex items-center justify-between p-4 bg-gray-50 rounded-lg"
            >
              <div className="flex items-center space-x-3">
                <div className={`p-2 rounded-full ${
                  activity.type === 'report' ? 'bg-blue-100 text-blue-600' :
                  activity.type === 'evaluation' ? 'bg-purple-100 text-purple-600' :
                  'bg-emerald-100 text-emerald-600'
                }`}>
                  {/* Add appropriate icon based on activity type */}
                </div>
                <div>
                  <p className="font-medium text-gray-900">{activity.title}</p>
                  <p className="text-sm text-gray-500">{activity.description}</p>
                </div>
              </div>
              <span className="text-sm text-gray-500">
                {new Date(activity.date).toLocaleDateString()}
              </span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default StudentDashboard;
