import React, { useState, useEffect } from 'react';
import { getInternshipDuration } from '../../services/studentApi';
import { toast } from 'react-hot-toast';
import { CalendarIcon, ClockIcon } from '@heroicons/react/24/outline';

const InternshipDuration = () => {
  const [duration, setDuration] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDuration = async () => {
      try {
        const response = await getInternshipDuration();
        setDuration(response.data);
      } catch (error) {
        console.error('Error fetching internship duration:', error);
        toast.error('Failed to load internship duration');
      } finally {
        setLoading(false);
      }
    };

    fetchDuration();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  if (!duration?.start_date) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-white shadow-sm rounded-lg p-6">
          <div className="text-center">
            <ClockIcon className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">No Active Internship</h3>
            <p className="mt-1 text-sm text-gray-500">
              You don't have any active internship at the moment.
            </p>
          </div>
        </div>
      </div>
    );
  }

  const startDate = new Date(duration.start_date);
  const endDate = new Date(duration.end_date);
  const today = new Date();

  const totalDays = Math.ceil((endDate - startDate) / (1000 * 60 * 60 * 24));
  const daysCompleted = Math.ceil((today - startDate) / (1000 * 60 * 60 * 24));
  const daysRemaining = Math.ceil((endDate - today) / (1000 * 60 * 60 * 24));
  const progressPercentage = Math.min(Math.round((daysCompleted / totalDays) * 100), 100);

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="bg-white shadow-sm rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h1 className="text-2xl font-semibold text-gray-900 mb-6">
            Internship Duration
          </h1>

          <div className="space-y-6">
            {/* Timeline Card */}
            <div className="bg-white border border-gray-200 rounded-lg p-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="flex items-center space-x-3">
                  <CalendarIcon className="h-6 w-6 text-emerald-500" />
                  <div>
                    <p className="text-sm text-gray-500">Start Date</p>
                    <p className="font-medium">{startDate.toLocaleDateString()}</p>
                  </div>
                </div>
                <div className="flex items-center space-x-3">
                  <CalendarIcon className="h-6 w-6 text-red-500" />
                  <div>
                    <p className="text-sm text-gray-500">End Date</p>
                    <p className="font-medium">{endDate.toLocaleDateString()}</p>
                  </div>
                </div>
              </div>

              {/* Progress Bar */}
              <div className="mt-6">
                <div className="relative">
                  <div className="overflow-hidden h-2 text-xs flex rounded bg-gray-200">
                    <div
                      style={{ width: `${progressPercentage}%` }}
                      className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-emerald-500"
                    />
                  </div>
                </div>
                <div className="mt-2 text-sm text-gray-600">
                  {progressPercentage}% completed
                </div>
              </div>

              {/* Stats */}
              <div className="mt-6 grid grid-cols-1 md:grid-cols-3 gap-6">
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Total Duration</p>
                  <p className="text-2xl font-semibold text-gray-900">{totalDays} days</p>
                </div>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Days Completed</p>
                  <p className="text-2xl font-semibold text-emerald-600">{daysCompleted} days</p>
                </div>
                <div className="bg-gray-50 p-4 rounded-lg">
                  <p className="text-sm text-gray-500">Days Remaining</p>
                  <p className="text-2xl font-semibold text-red-600">{daysRemaining} days</p>
                </div>
              </div>

              {/* Additional Information */}
              {duration.additional_info && (
                <div className="mt-6 border-t border-gray-200 pt-4">
                  <h4 className="text-sm font-medium text-gray-900 mb-2">
                    Additional Information
                  </h4>
                  <p className="text-gray-700 whitespace-pre-line">
                    {duration.additional_info}
                  </p>
                </div>
              )}

              {/* Schedule */}
              {duration.schedule && (
                <div className="mt-6 border-t border-gray-200 pt-4">
                  <h4 className="text-sm font-medium text-gray-900 mb-2">
                    Work Schedule
                  </h4>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {Object.entries(duration.schedule).map(([day, hours]) => (
                      <div key={day} className="flex justify-between">
                        <span className="text-gray-600 capitalize">{day}</span>
                        <span className="text-gray-900">{hours}</span>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InternshipDuration;
