import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  FileText,
  Users,
  Calendar,
  CheckCircle,
  Clock,
  BarChart3,
  BookOpen,
  ClipboardList,
  AlertCircle
} from 'lucide-react';
import { Button } from '../../components/UI/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/UI/Card';
import { toast } from 'react-hot-toast';
import studentApi from '../../services/studentApi';
import ViewMentorEvaluation from '../../components/student/ViewMentorEvaluation';
import ViewTeacherEvaluation from '../../components/student/ViewTeacherEvaluation';
import PreliminaryReportCheck from '../../components/student/PreliminaryReportCheck';
import ViewInternshipDuration from '../../components/student/ViewInternshipDuration';
import { useAuth } from '../../context/AuthContext';

// Create StatCard component for reuse
const StatCard = ({ title, value, icon: Icon, color, gradient }) => (
  <Card className={`bg-gradient-to-br ${gradient} text-white`}>
    <CardHeader className="flex flex-row items-center justify-between pb-2">
      <CardTitle className="text-sm font-medium text-white/90">{title}</CardTitle>
      <Icon className="h-4 w-4 text-white/70" />
    </CardHeader>
    <CardContent>
      <div className="text-3xl font-bold">{value}</div>
    </CardContent>
  </Card>
);

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    reportsSubmitted: 0,
    daysRemaining: 0,
    overallProgress: 0,
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const overview = await studentApi.dashboard.getOverview();
        const stats = await studentApi.dashboard.getStats();
        const activities = await studentApi.dashboard.getActivities();

        setDashboardData({
          ...overview,
          ...stats,
          recentActivity: activities
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  const stats = [
    {
      title: "Reports Submitted",
      value: dashboardData.reportsSubmitted,
      icon: FileText,
      gradient: "from-blue-500 to-blue-600"
    },
    {
      title: "Days Remaining",
      value: dashboardData.daysRemaining,
      icon: Calendar,
      gradient: "from-emerald-500 to-emerald-600"
    },
    {
      title: "Overall Progress",
      value: `${dashboardData.overallProgress}%`,
      icon: BarChart3,
      gradient: "from-purple-500 to-purple-600"
    }
  ];

  // Add new function for quick actions
  const handleQuickAction = (action) => {
    switch (action) {
      case 'submit-report':
        navigate('/dashboard/reports/submit');
        break;
      case 'register-internship':
        navigate('/dashboard/internship/register');
        break;
      case 'check-preliminary':
        document.getElementById('preliminary-check')?.scrollIntoView({ behavior: 'smooth' });
        break;
      default:
        break;
    }
  };

  // Add notification badge component
  const NotificationBadge = ({ count }) => (
    count > 0 ? (
      <span className="absolute -top-1 -right-1 h-4 w-4 rounded-full bg-red-500 text-xs text-white flex items-center justify-center">
        {count}
      </span>
    ) : null
  );

  return (
    <div className="space-y-6">
      {/* Welcome Section with improved styling */}
      <div className="bg-gradient-to-r from-emerald-500 to-blue-500 text-white rounded-lg shadow-lg p-6">
        <h1 className="text-3xl font-bold">
          Welcome back, {user?.first_name || 'Student'}
        </h1>
        <p className="mt-2 text-white/90">
          Track your internship progress and manage your tasks
        </p>
        {dashboardData.alerts && dashboardData.alerts.length > 0 && (
          <div className="mt-4 bg-white/10 rounded-lg p-4">
            <div className="flex items-center space-x-2">
              <AlertCircle className="h-5 w-5" />
              <span className="font-medium">Important Updates</span>
            </div>
            <ul className="mt-2 space-y-1">
              {dashboardData.alerts.map((alert, index) => (
                <li key={index} className="text-sm flex items-center space-x-2">
                  <span className="w-2 h-2 rounded-full bg-white" />
                  <span>{alert.message}</span>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>

      {/* Stats Overview with improved animations */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {stats.map((stat, index) => (
          <StatCard 
            key={index} 
            {...stat} 
            className="transform transition-all hover:scale-105"
          />
        ))}
      </div>

      {/* Quick Actions with improved UI */}
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        <Card className="hover:shadow-md transition-shadow">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <div className="w-12 h-12 rounded-lg bg-blue-100 flex items-center justify-center mb-4">
                  <FileText className="h-6 w-6 text-blue-600" />
                </div>
                <h3 className="font-medium text-gray-900">Submit Report</h3>
                <p className="text-sm text-gray-500 mt-1">Upload your weekly or monthly report</p>
              </div>
              <Button
                variant="outline"
                as={Link}
                to="/dashboard/reports"
                className="text-blue-600"
              >
                Submit
              </Button>
            </div>
          </CardContent>
        </Card>

        <Card className="hover:shadow-md transition-shadow">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <div className="w-12 h-12 rounded-lg bg-emerald-100 flex items-center justify-center mb-4">
                  <BookOpen className="h-6 w-6 text-emerald-600" />
                </div>
                <h3 className="font-medium text-gray-900">Register Internship</h3>
                <p className="text-sm text-gray-500 mt-1">Register for a new internship position</p>
              </div>
              <Button
                variant="outline"
                as={Link}
                to="/dashboard/internship"
                className="text-emerald-600"
              >
                Register
              </Button>
            </div>
          </CardContent>
        </Card>

        <Card className="hover:shadow-md transition-shadow">
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <div className="w-12 h-12 rounded-lg bg-purple-100 flex items-center justify-center mb-4">
                  <ClipboardList className="h-6 w-6 text-purple-600" />
                </div>
                <h3 className="font-medium text-gray-900">Preliminary Check</h3>
                <p className="text-sm text-gray-500 mt-1">Submit preliminary report check</p>
              </div>
              <Button
                variant="outline"
                className="text-purple-600"
                onClick={() => document.getElementById('preliminary-check').scrollIntoView({ behavior: 'smooth' })}
              >
                Check
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Progress Tracking Section */}
      <div className="grid gap-6 md:grid-cols-2">
        <ViewInternshipDuration />
        <PreliminaryReportCheck />
      </div>

      {/* Evaluations Section */}
      <div className="grid gap-6 md:grid-cols-2">
        <ViewMentorEvaluation />
        <ViewTeacherEvaluation />
      </div>

      {/* Recent Activity with improved styling */}
      {dashboardData.recentActivity && dashboardData.recentActivity.length > 0 && (
        <Card className="overflow-hidden">
          <CardHeader className="border-b bg-gray-50/50">
            <CardTitle className="text-lg font-medium">Recent Activity</CardTitle>
          </CardHeader>
          <CardContent className="p-0">
            <div className="divide-y divide-gray-100">
              {dashboardData.recentActivity.map((activity, index) => (
                <div 
                  key={index} 
                  className="p-4 hover:bg-gray-50 transition-colors flex items-center space-x-4"
                >
                  <div className="flex-shrink-0">
                    <div className="w-10 h-10 rounded-full bg-emerald-100 flex items-center justify-center">
                      <activity.icon className="h-5 w-5 text-emerald-600" />
                    </div>
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900 truncate">
                      {activity.title}
                    </p>
                    <p className="text-sm text-gray-500">
                      {activity.description}
                    </p>
                  </div>
                  <div className="flex-shrink-0 text-sm text-gray-500">
                    {new Date(activity.timestamp).toLocaleDateString()}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default StudentDashboard;
