import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-hot-toast';
import {
  BookOpen,
  Calendar,
  ClipboardList,
  FileText,
  UserCheck,
  Users,
  Activity
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/UI/Card';
import { Button } from '../../components/UI/Button';
import studentApi from '../../services/studentApi';

// Import student components
import RegisterInternship from '../../components/student/RegisterInternship';
import PreliminaryReportCheck from '../../components/student/PreliminaryReportCheck';
import SubmitReport from '../../components/student/SubmitReport';
import ViewInternshipDuration from '../../components/student/ViewInternshipDuration';
import ViewMentorEvaluation from '../../components/student/ViewMentorEvaluation';
import ViewTeacherEvaluation from '../../components/student/ViewTeacherEvaluation';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [dashboardData, setDashboardData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const data = await studentApi.dashboard.getOverview();
      setDashboardData(data);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      toast.error('Failed to load dashboard data');
    } finally {
      setLoading(false);
    }
  };

  const tabs = [
    { id: 'overview', label: 'Overview', icon: Activity },
    { id: 'internship', label: 'Internship', icon: BookOpen },
    { id: 'reports', label: 'Reports', icon: FileText },
    { id: 'evaluations', label: 'Evaluations', icon: UserCheck }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  const renderContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            <ViewInternshipDuration />
            <PreliminaryReportCheck />
            <Card className="col-span-full">
              <CardHeader>
                <CardTitle>Recent Activities</CardTitle>
              </CardHeader>
              <CardContent>
                {dashboardData?.recentActivities?.map((activity, index) => (
                  <div key={index} className="flex items-center space-x-4 py-2">
                    <div className="rounded-full bg-emerald-100 p-2">
                      <Activity className="h-4 w-4 text-emerald-500" />
                    </div>
                    <div>
                      <p className="text-sm font-medium">{activity.title}</p>
                      <p className="text-xs text-gray-500">{activity.timestamp}</p>
                    </div>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>
        );

      case 'internship':
        return (
          <div className="space-y-6">
            {!dashboardData?.hasActiveInternship ? (
              <RegisterInternship onSuccess={fetchDashboardData} />
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <ViewInternshipDuration />
                <Card>
                  <CardHeader>
                    <CardTitle>Current Internship</CardTitle>
                  </CardHeader>
                  <CardContent>
                    {/* Display current internship details */}
                  </CardContent>
                </Card>
              </div>
            )}
          </div>
        );

      case 'reports':
        return (
          <div className="space-y-6">
            <SubmitReport />
            <Card>
              <CardHeader>
                <CardTitle>Report History</CardTitle>
              </CardHeader>
              <CardContent>
                {dashboardData?.reports?.map((report, index) => (
                  <div key={index} className="flex items-center justify-between py-2">
                    <div className="flex items-center space-x-4">
                      <FileText className="h-5 w-5 text-gray-400" />
                      <div>
                        <p className="font-medium">{report.title}</p>
                        <p className="text-sm text-gray-500">{report.submitted_at}</p>
                      </div>
                    </div>
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                      report.status === 'approved' ? 'bg-green-100 text-green-600' :
                      report.status === 'pending' ? 'bg-yellow-100 text-yellow-600' :
                      'bg-red-100 text-red-600'
                    }`}>
                      {report.status}
                    </span>
                  </div>
                ))}
              </CardContent>
            </Card>
          </div>
        );

      case 'evaluations':
        return (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <ViewMentorEvaluation />
            <ViewTeacherEvaluation />
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="space-y-6 p-6">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name}
        </h1>
        <p className="mt-1 text-sm text-gray-500">
          Manage your internship progress and submissions
        </p>
      </div>

      {/* Navigation Tabs */}
      <div className="flex space-x-4 border-b">
        {tabs.map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`flex items-center space-x-2 px-4 py-2 border-b-2 font-medium text-sm transition-colors
              ${activeTab === tab.id
                ? 'border-emerald-500 text-emerald-600'
                : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
          >
            <tab.icon className="h-4 w-4" />
            <span>{tab.label}</span>
          </button>
        ))}
      </div>

      {/* Content Area */}
      <div className="mt-6">
        {renderContent()}
      </div>
    </div>
  );
};

export default StudentDashboard;
