  Award,
  CheckCircle,
  Briefcase,
  GraduationCap,
  Star
} from 'lucide-react';
import React, { useEffect, useState } from 'react';
import { toast } from 'react-hot-toast';
import { Link } from 'react-router-dom';
import { Button } from '../../components/UI/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/UI/Card';
import { useAuth } from '../../context/AuthContext';
import studentApi from '../../services/studentApi';

// Update StatCard to use emerald color scheme with new design
const StatCard = ({ title, value, icon: Icon, description, trend }) => (
  <Card className="hover:shadow-lg transition-shadow bg-white border border-gray-100">
    <CardContent className="p-6">
      <div className="flex items-center justify-between mb-4">
        <div className="w-12 h-12 rounded-lg bg-emerald-50 flex items-center justify-center">
          <Icon className="h-6 w-6 text-emerald-600" />
        </div>
        {trend && (
          <span className={`text-sm font-medium ${trend > 0 ? 'text-green-500' : 'text-red-500'}`}>
            {trend > 0 ? '+' : ''}{trend}%
          </span>
        )}
      </div>
      <div>
        <h3 className="text-2xl font-bold text-gray-900">{value}</h3>
        <p className="text-sm text-gray-500 mt-1">{title}</p>
      </div>
    </CardContent>
  </Card>
);

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    reportsSubmitted: 0,
    daysRemaining: 0,
    overallProgress: 0,
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const overview = await studentApi.dashboard.getOverview();
        const stats = await studentApi.dashboard.getStats();
        const activities = await studentApi.dashboard.getActivities();

        setDashboardData({
          ...overview,
          ...stats,
          recentActivity: activities
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  const stats = [
    {
      title: "Reports Submitted",
      value: dashboardData.reportsSubmitted,
      icon: FileText,
      trend: 12
    },
    {
      title: "Days Remaining",
      value: dashboardData.daysRemaining,
      icon: Calendar,
      trend: -5
    },
    {
      title: "Overall Progress",
      value: `${dashboardData.overallProgress}%`,
      icon: TrendingUp,
      trend: 8
    },
    {
      title: "Tasks Completed",
      value: "12/15",
      icon: CheckCircle,
      trend: 15
    }
  ];

  return (
    <div className="space-y-8">
      {/* Top Section with Welcome and Quick Stats */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Welcome Card */}
        <Card className="lg:col-span-2 bg-gradient-to-br from-emerald-500 to-emerald-600 text-white">
          <CardContent className="p-8">
            <div className="flex items-start justify-between">
              <div>
                <h1 className="text-3xl font-bold">
                  Welcome back, {user?.first_name || 'Student'}! 👋
                </h1>
                <p className="mt-2 text-emerald-50">
                  Your internship journey is {dashboardData.overallProgress}% complete
                </p>
              </div>
              <div className="w-16 h-16 rounded-full bg-emerald-400/30 flex items-center justify-center">
                <GraduationCap className="h-8 w-8 text-white" />
              </div>
            </div>

            <div className="mt-8">
              <div className="flex justify-between mb-2">
                <span>Overall Progress</span>
                <span>{dashboardData.overallProgress}%</span>
              </div>
              <div className="w-full bg-emerald-400/30 rounded-full h-2">
                <div
                  className="bg-white h-2 rounded-full"
                  style={{ width: `${dashboardData.overallProgress}%` }}
                />
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Quick Actions Card */}
        <Card className="bg-white">
          <CardHeader className="pb-2">
            <CardTitle className="text-lg font-semibold">Quick Actions</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              <Button
                as={Link}
                to="/dashboard/reports/new"
                className="w-full justify-start bg-emerald-50 text-emerald-600 hover:bg-emerald-100"
              >
                <FileText className="h-4 w-4 mr-2" />
                Submit New Report
              </Button>
              <Button
                as={Link}
                to="/dashboard/tasks"
                className="w-full justify-start bg-emerald-50 text-emerald-600 hover:bg-emerald-100"
              >
                <ClipboardList className="h-4 w-4 mr-2" />
                View Tasks
              </Button>
              <Button
                as={Link}
                to="/dashboard/schedule"
                className="w-full justify-start bg-emerald-50 text-emerald-600 hover:bg-emerald-100"
              >
                <Calendar className="h-4 w-4 mr-2" />
                Check Schedule
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {stats.map((stat, index) => (
          <StatCard key={index} {...stat} />
        ))}
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Upcoming Tasks */}
        <Card>
          <CardHeader className="border-b p-6">
            <CardTitle className="text-lg font-semibold">Upcoming Tasks</CardTitle>
          </CardHeader>
          <CardContent className="p-6">
            <div className="space-y-4">
              {[1, 2, 3].map((_, index) => (
                <div key={index} className="flex items-center p-4 bg-gray-50 rounded-lg">
                  <div className="w-10 h-10 rounded-lg bg-emerald-100 flex items-center justify-center mr-4">
                    <ClipboardList className="h-5 w-5 text-emerald-600" />
                  </div>
                  <div className="flex-1">
                    <h4 className="text-sm font-medium text-gray-900">Weekly Report Submission</h4>
                    <p className="text-sm text-gray-500">Due in 2 days</p>
                  </div>
                  <Button variant="outline" size="sm" className="text-emerald-600">
                    View
                  </Button>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>

        {/* Recent Activity */}
        <Card>
          <CardHeader className="border-b p-6">
            <CardTitle className="text-lg font-semibold">Recent Activity</CardTitle>
          </CardHeader>
          <CardContent className="p-6">
            <div className="space-y-6">
              {dashboardData.recentActivity.map((activity, index) => (
                <div key={index} className="flex items-start space-x-4">
                  <div className="w-8 h-8 rounded-lg bg-emerald-50 flex items-center justify-center flex-shrink-0">
                    <activity.icon className="h-4 w-4 text-emerald-600" />
                  </div>
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium text-gray-900">{activity.title}</p>
                    <p className="text-sm text-gray-500">{activity.description}</p>
                    <p className="text-xs text-gray-400 mt-1">
                      {new Date(activity.timestamp).toLocaleDateString()}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Performance Overview */}
      <Card>
        <CardHeader className="border-b p-6">
          <CardTitle className="text-lg font-semibold">Performance Overview</CardTitle>
        </CardHeader>
        <CardContent className="p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="p-4 bg-emerald-50 rounded-lg">
              <div className="flex items-center justify-between mb-4">
                <Star className="h-5 w-5 text-emerald-600" />
                <span className="text-sm text-emerald-600">This Month</span>
              </div>
              <h3 className="text-2xl font-bold text-gray-900">95%</h3>
              <p className="text-sm text-gray-500">Attendance Rate</p>
            </div>
            <div className="p-4 bg-emerald-50 rounded-lg">
              <div className="flex items-center justify-between mb-4">
                <Award className="h-5 w-5 text-emerald-600" />
                <span className="text-sm text-emerald-600">Overall</span>
              </div>
              <h3 className="text-2xl font-bold text-gray-900">A+</h3>
              <p className="text-sm text-gray-500">Performance Grade</p>
            </div>
            <div className="p-4 bg-emerald-50 rounded-lg">
              <div className="flex items-center justify-between mb-4">
                <Briefcase className="h-5 w-5 text-emerald-600" />
                <span className="text-sm text-emerald-600">Completed</span>
              </div>
              <h3 className="text-2xl font-bold text-gray-900">24</h3>
              <p className="text-sm text-gray-500">Total Tasks</p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default StudentDashboard;
