import api from './api';

const studentApi = {
    // Dashboard Overview
    getDashboardData: async () => {
        try {
            const response = await api.get('/api/dashboard/student/');
            return response.data;
        } catch (error) {
            console.error('Error fetching student dashboard:', error);
            throw error;
        }
    },

    // Student Stats
    getStats: async () => {
        try {
            const response = await api.get('/api/users/stats/');
            return response.data;
        } catch (error) {
            console.error('Error fetching student stats:', error);
            throw error;
        }
    },

    // Preliminary Report
    getPreliminaryStatus: async () => {
        try {
            const response = await api.get('/api/reports/preliminary/status/');
            return response.data;
        } catch (error) {
            console.error('Error fetching preliminary status:', error);
            throw error;
        }
    },

    // Submit Report
    submitReport: async (data) => {
        try {
            const response = await api.post('/api/reports/submit/', data);
            return response.data;
        } catch (error) {
            console.error('Error submitting report:', error);
            throw error;
        }
    },

    // Get Active Internship
    getActiveInternship: async () => {
        try {
            const response = await api.get('/api/internships/current/');
            return response.data;
        } catch (error) {
            console.error('Error fetching active internship:', error);
            throw error;
        }
    },

    // Get Recent Activities
    getRecentActivities: async () => {
        try {
            const response = await api.get('/api/dashboard/student/activities/');
            return response.data;
        } catch (error) {
            console.error('Error fetching recent activities:', error);
            throw error;
        }
    }
};

export default studentApi;
