import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { getPreliminaryReportStatus, submitPreliminaryReport } from '../../services/studentApi';
import { toast } from 'react-hot-toast';
import Button from '../../components/UI/Button';

const PreliminaryCheck = () => {
  const [report, setReport] = useState(null);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [newReport, setNewReport] = useState({
    title: '',
    content: '',
    attachments: []
  });
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStatus = async () => {
      try {
        const response = await getPreliminaryReportStatus();
        setReport(response.data);
      } catch (error) {
        console.error('Error fetching preliminary report status:', error);
        toast.error('Failed to load report status');
      } finally {
        setLoading(false);
      }
    };

    fetchStatus();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitting(true);

    try {
      const formData = new FormData();
      formData.append('title', newReport.title);
      formData.append('content', newReport.content);
      newReport.attachments.forEach(file => {
        formData.append('attachments', file);
      });

      await submitPreliminaryReport(formData);
      toast.success('Preliminary report submitted successfully');
      navigate('/dashboard');
    } catch (error) {
      console.error('Submission error:', error);
      toast.error(error.message || 'Failed to submit report');
    } finally {
      setSubmitting(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewReport(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileChange = (e) => {
    setNewReport(prev => ({
      ...prev,
      attachments: Array.from(e.target.files)
    }));
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="bg-white shadow-sm rounded-lg">
        <div className="px-4 py-5 sm:p-6">
          <h1 className="text-2xl font-semibold text-gray-900 mb-6">
            Preliminary Report Check
          </h1>

          {report ? (
            <div className="space-y-6">
              <div className="bg-white border border-gray-200 rounded-lg p-6">
                <div className="flex items-center justify-between mb-4">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900">
                      {report.title}
                    </h3>
                    <p className="text-sm text-gray-500">
                      Submitted on: {new Date(report.submitted_at).toLocaleDateString()}
                    </p>
                  </div>
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                    report.status === 'approved' 
                      ? 'bg-green-100 text-green-800'
                      : report.status === 'rejected'
                      ? 'bg-red-100 text-red-800'
                      : 'bg-yellow-100 text-yellow-800'
                  }`}>
                    {report.status}
                  </span>
                </div>

                {report.feedback && (
                  <div className="mt-4 border-t border-gray-200 pt-4">
                    <h4 className="text-sm font-medium text-gray-900 mb-2">
                      Reviewer's Feedback
                    </h4>
                    <p className="text-gray-700 whitespace-pre-line">
                      {report.feedback}
                    </p>
                  </div>
                )}

                {report.required_changes && (
                  <div className="mt-4 border-t border-gray-200 pt-4">
                    <h4 className="text-sm font-medium text-gray-900 mb-2">
                      Required Changes
                    </h4>
                    <ul className="list-disc list-inside text-gray-700 space-y-1">
                      {report.required_changes.map((change, idx) => (
                        <li key={idx}>{change}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            </div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-6">
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Report Title
                </label>
                <input
                  type="text"
                  name="title"
                  value={newReport.title}
                  onChange={handleChange}
                  required
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-emerald-500 focus:border-emerald-500 sm:text-sm"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Report Content
                </label>
                <textarea
                  name="content"
                  rows={10}
                  value={newReport.content}
                  onChange={handleChange}
                  required
                  className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-emerald-500 focus:border-emerald-500 sm:text-sm"
                  placeholder="Write your preliminary report here..."
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Attachments
                </label>
                <input
                  type="file"
                  multiple
                  onChange={handleFileChange}
                  className="mt-1 block w-full text-sm text-gray-500
                    file:mr-4 file:py-2 file:px-4
                    file:rounded-md file:border-0
                    file:text-sm file:font-semibold
                    file:bg-emerald-50 file:text-emerald-700
                    hover:file:bg-emerald-100"
                />
              </div>

              <div className="flex justify-end space-x-3">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => navigate('/dashboard')}
                >
                  Cancel
                </Button>
                <Button
                  type="submit"
                  variant="primary"
                  loading={submitting}
                >
                  Submit Report
                </Button>
              </div>
            </form>
          )}
        </div>
      </div>
    </div>
  );
};

export default PreliminaryCheck;
