"use client"

import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getStudentDashboard, getStudentStats } from '../../services/studentApi';
import { useAuth } from '../../context/AuthContext';
import { toast } from 'react-hot-toast';
import {
  DocumentTextIcon,
  ClipboardDocumentCheckIcon,
  UserGroupIcon,
  CalendarIcon,
  ChartBarIcon,
  UserIcon,
  DocumentCheckIcon,
  ClockIcon,
} from '@heroicons/react/24/outline';
import Button from '../../components/UI/Button';

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState(null);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const [dashboardResponse, statsResponse] = await Promise.all([
          getStudentDashboard(),
          getStudentStats()
        ]);
        setDashboardData(dashboardResponse.data);
        setStats(statsResponse.data);
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const QuickActionButton = ({ to, icon: Icon, title, description, color }) => (
    <Link
      to={to}
      className="bg-white overflow-hidden shadow rounded-lg hover:shadow-md transition-shadow duration-200"
    >
      <div className="p-5">
        <div className={`${color} rounded-lg w-12 h-12 flex items-center justify-center mb-4`}>
          <Icon className="h-6 w-6 text-white" />
        </div>
        <h3 className="text-lg font-medium text-gray-900">{title}</h3>
        <p className="mt-2 text-sm text-gray-500">{description}</p>
      </div>
    </Link>
  );

  const quickActions = [
    {
      to: "/dashboard/submit-report",
      icon: DocumentTextIcon,
      title: "Submit Report",
      description: "Submit your weekly or monthly report",
      color: "bg-blue-500"
    },
    {
      to: "/dashboard/register-internship",
      icon: ClipboardDocumentCheckIcon,
      title: "Register Internship",
      description: "Register for a new internship",
      color: "bg-emerald-500"
    },
    {
      to: "/dashboard/mentor-evaluation",
      icon: UserGroupIcon,
      title: "Mentor Evaluation",
      description: "View your mentor's feedback",
      color: "bg-purple-500"
    },
    {
      to: "/dashboard/teacher-evaluation",
      icon: ChartBarIcon,
      title: "Teacher Evaluation",
      description: "Check teacher evaluations",
      color: "bg-yellow-500"
    },
    {
      to: "/dashboard/edit-profile",
      icon: UserIcon,
      title: "Edit Profile",
      description: "Update your information",
      color: "bg-pink-500"
    },
    {
      to: "/dashboard/preliminary-check",
      icon: DocumentCheckIcon,
      title: "Preliminary Check",
      description: "Check preliminary report status",
      color: "bg-indigo-500"
    }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
        <h1 className="text-2xl font-bold text-gray-900">
          Welcome back, {user?.first_name || 'Student'}
        </h1>
        <p className="mt-1 text-sm text-gray-500">
          Here's an overview of your internship progress
        </p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-6">
        <StatCard
          title="Reports Submitted"
          value={stats?.reports_submitted || 0}
          icon={DocumentTextIcon}
          color="text-blue-600"
        />
        <StatCard
          title="Days Remaining"
          value={stats?.days_remaining || 0}
          icon={CalendarIcon}
          color="text-emerald-600"
        />
        <StatCard
          title="Tasks Completed"
          value={stats?.tasks_completed || 0}
          icon={ClipboardDocumentCheckIcon}
          color="text-purple-600"
        />
        <StatCard
          title="Overall Progress"
          value={`${stats?.progress || 0}%`}
          icon={ChartBarIcon}
          color="text-yellow-600"
        />
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-6">
        {quickActions.map((action, index) => (
          <QuickActionButton key={index} {...action} />
        ))}
      </div>

      {/* Recent Activity */}
      {dashboardData?.recentActivity && dashboardData.recentActivity.length > 0 && (
        <div className="bg-white shadow rounded-lg">
          <div className="px-4 py-5 sm:p-6">
            <h2 className="text-lg font-medium text-gray-900 mb-4">Recent Activity</h2>
            <div className="flow-root">
              <ul role="list" className="-mb-8">
                {dashboardData.recentActivity.map((activity, index) => (
                  <li key={index}>
                    <div className="relative pb-8">
                      {index !== dashboardData.recentActivity.length - 1 && (
                        <span
                          className="absolute top-4 left-4 -ml-px h-full w-0.5 bg-gray-200"
                          aria-hidden="true"
                        />
                      )}
                      <div className="relative flex space-x-3">
                        <div>
                          <span className={`h-8 w-8 rounded-full flex items-center justify-center ring-8 ring-white ${
                            activity.type === 'report' ? 'bg-blue-500' :
                            activity.type === 'evaluation' ? 'bg-purple-500' :
                            'bg-emerald-500'
                          }`}>
                            <activity.icon className="h-5 w-5 text-white" aria-hidden="true" />
                          </span>
                        </div>
                        <div className="min-w-0 flex-1 pt-1.5 flex justify-between space-x-4">
                          <div>
                            <p className="text-sm text-gray-500">
                              {activity.description}
                            </p>
                          </div>
                          <div className="text-right text-sm whitespace-nowrap text-gray-500">
                            <time dateTime={activity.datetime}>{activity.date}</time>
                          </div>
                        </div>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const StatCard = ({ title, value, icon: Icon, color }) => (
  <div className="bg-white overflow-hidden shadow rounded-lg">
    <div className="p-5">
      <div className="flex items-center">
        <div className="flex-shrink-0">
          <Icon className={`h-6 w-6 ${color}`} />
        </div>
        <div className="ml-5 w-0 flex-1">
          <dl>
            <dt className="text-sm font-medium text-gray-500 truncate">{title}</dt>
            <dd className="text-lg font-semibold text-gray-900">{value}</dd>
          </dl>
        </div>
      </div>
    </div>
  </div>
);

export default StudentDashboard;
