import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  FileText,
  Users,
  Calendar,
  CheckCircle,
  Clock,
  BarChart3,
  BookOpen,
  ClipboardList
} from 'lucide-react';
import { Button } from '../../components/UI/Button';
import { Card, CardContent, CardHeader, CardTitle } from '../../components/UI/Card';
import { toast } from 'react-hot-toast';
import studentApi from '../../services/studentApi';
import Progress from '../../components/UI/Progress';

// Create StatCard component for reuse
const StatCard = ({ title, value, icon: Icon, color, gradient }) => (
  <Card className={`bg-gradient-to-br ${gradient} text-white`}>
    <CardHeader className="flex flex-row items-center justify-between pb-2">
      <CardTitle className="text-sm font-medium text-white/90">{title}</CardTitle>
      <Icon className="h-4 w-4 text-white/70" />
    </CardHeader>
    <CardContent>
      <div className="text-3xl font-bold">{value}</div>
    </CardContent>
  </Card>
);

const StudentDashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    reportsSubmitted: 0,
    daysRemaining: 0,
    overallProgress: 0,
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const overview = await studentApi.dashboard.getOverview();
        const stats = await studentApi.dashboard.getStats();
        const activities = await studentApi.dashboard.getActivities();

        setDashboardData({
          ...overview,
          ...stats,
          recentActivity: activities
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  const stats = [
    {
      title: "Reports Submitted",
      value: dashboardData.reportsSubmitted,
      icon: FileText,
      gradient: "from-blue-500 to-blue-600"
    },
    {
      title: "Days Remaining",
      value: dashboardData.daysRemaining,
      icon: Calendar,
      gradient: "from-emerald-500 to-emerald-600"
    },
    {
      title: "Overall Progress",
      value: `${dashboardData.overallProgress}%`,
      icon: ChartBar,
      gradient: "from-purple-500 to-purple-600"
    }
  ];

  const quickActions = [
    {
      title: "Submit Report",
      description: "Upload your weekly or monthly internship report",
      icon: FileText,
      path: "/dashboard/reports",
      color: "blue"
    },
    {
      title: "Register Internship",
      description: "Register for a new internship position",
      icon: BookOpen,
      path: "/dashboard/internship",
      color: "emerald"
    },
    {
      title: "View Evaluations",
      description: "Check your mentor and teacher evaluations",
      icon: ClipboardList,
      path: "/dashboard/evaluations",
      color: "purple"
    }
  ];

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Welcome Section */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <h1 className="text-2xl font-bold text-gray-900">Welcome back, Student</h1>
        <p className="mt-1 text-sm text-gray-500">Track your internship progress and tasks</p>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {stats.map((stat, index) => (
          <StatCard key={index} {...stat} />
        ))}
      </div>

      {/* Quick Actions */}
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {quickActions.map((action, index) => (
          <Card key={index} className="hover:shadow-md transition-shadow">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <div className={`w-12 h-12 rounded-lg bg-${action.color}-100 flex items-center justify-center mb-4`}>
                    <action.icon className={`h-6 w-6 text-${action.color}-600`} />
                  </div>
                  <h3 className="font-medium text-gray-900">{action.title}</h3>
                  <p className="text-sm text-gray-500 mt-1">{action.description}</p>
                </div>
                <Button
                  variant="outline"
                  as={Link}
                  to={action.path}
                  className={`text-${action.color}-600 hover:bg-${action.color}-50`}
                >
                  View
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Progress Section */}
      <Card>
        <CardHeader>
          <CardTitle>Internship Progress</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-6">
            <div>
              <div className="flex justify-between text-sm mb-2">
                <span className="text-gray-500">Overall Completion</span>
                <span className="font-medium">{dashboardData.overallProgress}%</span>
              </div>
              <Progress value={dashboardData.overallProgress} color="emerald" size="lg" />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Recent Activity */}
      {dashboardData.recentActivity.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle>Recent Activity</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {dashboardData.recentActivity.map((activity, index) => (
                <div key={index} className="flex items-center space-x-4">
                  <div className={`p-2 rounded-full bg-${activity.type === 'report' ? 'blue' : 'emerald'}-100`}>
                    {activity.type === 'report' ? (
                      <FileText className={`h-5 w-5 text-${activity.type === 'report' ? 'blue' : 'emerald'}-600`} />
                    ) : (
                      <CheckCircle className={`h-5 w-5 text-${activity.type === 'report' ? 'blue' : 'emerald'}-600`} />
                    )}
                  </div>
                  <div className="flex-1">
                    <p className="text-sm font-medium text-gray-900">{activity.title}</p>
                    <p className="text-sm text-gray-500">{activity.description}</p>
                  </div>
                  <span className="text-sm text-gray-500">
                    {new Date(activity.timestamp).toLocaleDateString()}
                  </span>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default StudentDashboard;
