import React from 'react';
import { Link } from 'react-router-dom';
import { Briefcase, Plus } from 'lucide-react';

const InternshipList = () => {
  // Replace this with actual data fetched from your API
  const internships = [
    { id: 1, title: 'Software Engineer Intern', company: 'Tech Corp', location: 'New York, NY' },
    { id: 2, title: 'Data Analyst Intern', company: 'Data Inc.', location: 'San Francisco, CA' },
  ];

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <h2 className="text-2xl font-bold text-gray-800">Available Internships</h2>
        <Link
          to="apply"
          className="inline-flex items-center px-4 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700"
        >
          <Plus className="h-5 w-5 mr-2" />
          Apply Now
        </Link>
      </div>

      <div className="bg-white rounded-lg shadow">
        <div className="divide-y">
          {internships.map((internship) => (
            <Link
              key={internship.id}
              to={`${internship.id}`}
              className="flex items-center p-4 hover:bg-gray-50"
            >
              <Briefcase className="h-6 w-6 text-emerald-600 mr-4" />
              <div className="flex-1">
                <h3 className="text-lg font-medium text-gray-900">{internship.title}</h3>
                <p className="text-sm text-gray-500">{internship.company} - {internship.location}</p>
              </div>
            </Link>
          ))}
        </div>
      </div>
    </div>
  );
};

export default InternshipList;
