import React from 'react'
import { BrowserRouter as Router, Route, Routes, Navigate, Outlet } from 'react-router-dom'
import { Toaster } from 'react-hot-toast'

// Layout
import DashboardLayout from './layouts/DashboardLayout'

// Pages
import Home from './pages/Home'
import Login from './pages/auth/Login'
import Register from './pages/auth/Register'

// Dashboards
import StudentDashboard from './pages/dashboard/StudentDashboard'
import MentorDashboard from './pages/dashboard/MentorDashboard'
import TeacherDashboard from './pages/dashboard/TeacherDashboard'
import AdminDashboard from './pages/dashboard/AdminDashboard'

// Student Components
import SubmitReport from './components/student/SubmitReport'
import RegisterInternship from './components/student/RegisterInternship'
import EditProfile from './components/student/EditProfile'
import ViewTeacherEvaluation from './components/student/ViewTeacherEvaluation'
import ViewMentorEvaluation from './components/student/ViewMentorEvaluation'

// Mentor Components
import MonitorStudents from './components/mentor/MonitorStudents'
import ReportReview from './components/mentor/ReportReview'
import DataStatistics from './components/mentor/DataStatistics'
import StudentAdvice from './components/mentor/StudentAdvice'
import MentorReport from './components/mentor/MentorReport'

// Teacher Components
import MonitorStudentProgress from './components/teacher/MonitorStudentProgress'
import FinalEvaluation from './components/teacher/FinalEvaluation'
import MonitorMentorEvaluation from './components/teacher/MonitorMentorEvaluation'

// Auth & Protection
import ProtectedRoute from './components/Auth/ProtectedRoute'
import { useAuth } from './context/AuthContext'

const App = () => {
  const { user } = useAuth()
  const role = user?.role || 'student'

  // Get the appropriate dashboard component based on user role
  const getDashboardComponent = () => {
    switch (role) {
      case 'mentor':
        return <MentorDashboard />
      case 'teacher':
        return <TeacherDashboard />
      case 'admin':
        return <AdminDashboard />
      default:
        return <StudentDashboard />
    }
  }

  // Role-based route configurations
  const getRoleRoutes = () => {
    switch (role) {
      case 'mentor':
        return [
          { path: 'monitor', element: <MonitorStudents /> },
          { path: 'review', element: <ReportReview /> },
          { path: 'statistics', element: <DataStatistics /> },
          { path: 'advice', element: <StudentAdvice /> },
          { path: 'reports/generate', element: <MentorReport /> },
        ]
      case 'student':
        return [
          { path: 'reports', element: <SubmitReport /> },
          { path: 'internship', element: <RegisterInternship /> },
          { path: 'profile', element: <EditProfile /> },
          { path: 'evaluations/teacher', element: <ViewTeacherEvaluation /> },
          { path: 'evaluations/mentor', element: <ViewMentorEvaluation /> },
        ]
      case 'teacher':
        return [
          { path: 'students', element: <MonitorStudentProgress /> },
          { path: 'evaluations', element: <FinalEvaluation /> },
          { path: 'mentor-evaluations', element: <MonitorMentorEvaluation /> },
        ]
      default:
        return []
    }
  }

  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        <Toaster position="top-right" />
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Dashboard Routes */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <DashboardLayout>
                  <Outlet />
                </DashboardLayout>
              </ProtectedRoute>
            }
          >
            {/* Dashboard Home */}
            <Route index element={getDashboardComponent()} />

            {/* Role-specific routes */}
            {getRoleRoutes().map(({ path, element }) => (
              <Route
                key={path}
                path={path}
                element={element}
              />
            ))}
          </Route>

          {/* Catch all route */}
          <Route path="*" element={<Navigate to="/dashboard" replace />} />
        </Routes>
      </div>
    </Router>
  )
}

export default App
