import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import {
  FileText,
  Users,
  Calendar,
  CheckCircle,
  Clock,
  BarChart3,
  BookOpen,
  ClipboardList,
  Briefcase,
  Home,
  Bell,
  User
} from 'lucide-react';
import { Button } from '../../components/UI/Button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '../../components/UI/Card';
import { toast } from 'react-hot-toast';
import studentApi from '../../services/studentApi';
import ViewMentorEvaluation from '../../components/student/ViewMentorEvaluation';
import ViewTeacherEvaluation from '../../components/student/ViewTeacherEvaluation';
import PreliminaryReportCheck from '../../components/student/PreliminaryReportCheck';
import ViewInternshipDuration from '../../components/student/ViewInternshipDuration';
import { useAuth } from '../../context/AuthContext';
import { Progress } from '../../components/UI/Progress';

// Create StatCard component for reuse
const StatCard = ({ title, value, icon: Icon, color, gradient, change }) => (
  <Card className={`bg-gradient-to-br ${gradient} text-white`}>
    <CardHeader className="flex flex-row items-center justify-between pb-2">
      <CardTitle className="text-sm font-medium text-white/90">{title}</CardTitle>
      <Icon className="h-4 w-4 text-white/70" />
    </CardHeader>
    <CardContent>
      <div className="text-3xl font-bold">{value}</div>
      {change && <p className="text-sm text-gray-500 mt-1">{change}</p>}
    </CardContent>
  </Card>
);

const StudentDashboard = () => {
  const { user } = useAuth();
  const [dashboardData, setDashboardData] = useState({
    reportsSubmitted: 0,
    daysRemaining: 0,
    overallProgress: 0,
    recentActivity: []
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        const overview = await studentApi.dashboard.getOverview();
        const stats = await studentApi.dashboard.getStats();
        const activities = await studentApi.dashboard.getActivities();

        setDashboardData({
          ...overview,
          ...stats,
          recentActivity: activities
        });
      } catch (error) {
        console.error('Error fetching dashboard data:', error);
        toast.error('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();
  }, []);

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-emerald-500"></div>
      </div>
    );
  }

  const stats = [
    {
      title: "Reports Submitted",
      value: dashboardData.reportsSubmitted,
      icon: FileText,
      gradient: "from-blue-500 to-blue-600",
      change: "+2 this week"
    },
    {
      title: "Days Remaining",
      value: dashboardData.daysRemaining,
      icon: Calendar,
      gradient: "from-emerald-500 to-emerald-600",
      change: "120 total days"
    },
    {
      title: "Overall Progress",
      value: `${dashboardData.overallProgress}%`,
      icon: BarChart3,
      gradient: "from-purple-500 to-purple-600",
      change: "On track"
    }
  ];

  const ProgressSection = ({ progress }) => (
    <Card>
      <CardHeader>
        <CardTitle>Internship Progress</CardTitle>
        <CardDescription>Track your overall internship completion</CardDescription>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <Progress value={progress} className="h-2" />
          <div className="flex justify-between text-sm text-gray-500">
            <span>Start</span>
            <span>{progress}% Complete</span>
            <span>End</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="space-y-6">
      {/* Welcome Section with Progress */}
      <div className="grid gap-6 md:grid-cols-2">
        <div className="bg-white rounded-lg shadow-sm p-6">
          <h1 className="text-2xl font-bold text-gray-900">
            Welcome back, {user?.first_name || 'Student'}
          </h1>
          <p className="mt-1 text-sm text-gray-500">
            Track your internship progress and tasks
          </p>
          <div className="mt-4">
            <Progress value={dashboardData.overallProgress} className="h-2" />
            <p className="mt-2 text-sm text-gray-600">
              {dashboardData.overallProgress}% of your internship completed
            </p>
          </div>
        </div>

        <Card>
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
          </CardHeader>
          <CardContent className="grid grid-cols-2 gap-4">
            <Button variant="outline" className="w-full" as={Link} to="/dashboard/reports/submit">
              <FileText className="mr-2 h-4 w-4" />
              Submit Report
            </Button>
            <Button variant="outline" className="w-full" as={Link} to="/dashboard/internship">
              <Briefcase className="mr-2 h-4 w-4" />
              View Internship
            </Button>
          </CardContent>
        </Card>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
        {stats.map((stat, index) => (
          <StatCard key={index} {...stat} />
        ))}
      </div>

      {/* Progress and Duration */}
      <div className="grid gap-6 md:grid-cols-2">
        <ViewInternshipDuration />
        <PreliminaryReportCheck />
      </div>

      {/* Evaluations */}
      <div className="grid gap-6 md:grid-cols-2">
        <ViewMentorEvaluation />
        <ViewTeacherEvaluation />
      </div>

      {/* Recent Activity */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Activity</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {dashboardData.recentActivity.map((activity, index) => (
              <div key={index} className="flex items-center space-x-4">
                <div className={`p-2 rounded-full bg-${activity.color}-100`}>
                  <activity.icon className={`h-4 w-4 text-${activity.color}-600`} />
                </div>
                <div className="flex-1 space-y-1">
                  <p className="text-sm font-medium text-gray-900">{activity.title}</p>
                  <p className="text-sm text-gray-500">{activity.description}</p>
                </div>
                <span className="text-sm text-gray-500">
                  {new Date(activity.timestamp).toLocaleDateString()}
                </span>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
};

export default StudentDashboard;
