import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-hot-toast';
import { UserCog } from 'lucide-react';
import { Button } from '../UI/Button';
import { Card, CardHeader, CardContent, CardTitle, CardDescription } from '../UI/Card';
import studentApi from '../../services/studentApi';

const EditProfile = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const data = await studentApi.profile.getProfile();
        setProfile(data);
      } catch (err) {
        setError('Failed to load profile');
        toast.error('Failed to load profile');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError(null);

    try {
      await studentApi.profile.updateProfile(profile);
      toast.success('Profile updated successfully');
      navigate('/dashboard');
    } catch (err) {
      setError('Failed to update profile');
      toast.error('Failed to update profile');
    } finally {
      setSaving(false);
    }
  };

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('image', file);

    try {
      await studentApi.profile.uploadProfilePicture(formData);
      toast.success('Profile picture updated');
    } catch (err) {
      toast.error('Failed to upload profile picture');
    }
  };

  if (loading) {
    return <div>Loading profile...</div>;
  }

  return (
    <Card className="max-w-2xl mx-auto">
      <CardHeader>
        <div className="rounded-lg bg-pink-500/10 p-2 w-fit">
          <UserCog className="h-6 w-6 text-pink-500" />
        </div>
        <CardTitle>Edit Profile</CardTitle>
        <CardDescription>Update your personal information</CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Form fields */}
          <div className="grid grid-cols-2 gap-4">
            {/* Add your form fields here */}
          </div>
          
          {/* Profile Picture Upload */}
          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700">
              Profile Picture
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="mt-1"
            />
          </div>

          <Button
            type="submit"
            className="w-full"
            disabled={saving}
            loading={saving}
          >
            {saving ? 'Saving...' : 'Save Changes'}
          </Button>

          {error && (
            <p className="text-red-500 text-sm mt-2">{error}</p>
          )}
        </form>
      </CardContent>
    </Card>
  );
};

export default EditProfile;
