import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import {
  LayoutDashboard,
  Users,
  FileText,
  Star,
  BarChart
} from 'lucide-react';

const TeacherDashboardLayout = ({ children }) => {
  const location = useLocation();

  const navigation = [
    { name: 'Dashboard', icon: LayoutDashboard, href: '/teacher' },
    { name: 'Students', icon: Users, href: '/teacher/students' },
    { name: 'Reports', icon: FileText, href: '/teacher/reports/review' },
    { name: 'Evaluations', icon: Star, href: '/teacher/evaluations' },
    { name: 'Analytics', icon: BarChart, href: '/teacher/analytics' }
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <div className="h-full">
      {/* Side Navigation */}
      <nav className="fixed top-16 left-0 w-64 h-[calc(100vh-4rem)] bg-white border-r border-gray-200 overflow-y-auto">
        <div className="px-4 py-5 space-y-1">
          {navigation.map((item) => {
            const Icon = item.icon;
            return (
              <Link
                key={item.name}
                to={item.href}
                className={`group flex items-center px-2 py-2 text-sm font-medium rounded-md ${
                  isActive(item.href)
                    ? 'bg-emerald-100 text-emerald-900'
                    : 'text-gray-600 hover:bg-gray-50 hover:text-gray-900'
                }`}
              >
                <Icon
                  className={`mr-3 h-5 w-5 ${
                    isActive(item.href)
                      ? 'text-emerald-500'
                      : 'text-gray-400 group-hover:text-gray-500'
                  }`}
                />
                {item.name}
              </Link>
            );
          })}
        </div>
      </nav>

      {/* Main Content */}
      <main className="pl-64">
        <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          {children}
        </div>
      </main>
    </div>
  );
};

export default TeacherDashboardLayout;
