import React from 'react'
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom'
import { Toaster } from 'react-hot-toast'
import Home from './pages/Home'
import Login from './pages/auth/Login'
import Register from './pages/auth/Register'
import DashboardLayout from './layouts/DashboardLayout'
import StudentDashboard from './pages/dashboard/StudentDashboard'
import MentorDashboard from './pages/dashboard/MentorDashboard'
import TeacherDashboard from './pages/dashboard/TeacherDashboard'
import AdminDashboard from './pages/dashboard/AdminDashboard'
import ProtectedRoute from './components/Auth/ProtectedRoute'
import { useAuth } from './context/AuthContext'

// Role-based dashboard component selector
const DashboardSelector = () => {
  const { user } = useAuth()

  switch (user?.role) {
    case 'mentor':
      return <MentorDashboard />
    case 'teacher':
      return <TeacherDashboard />
    case 'admin':
      return <AdminDashboard />
    case 'student':
      return <StudentDashboard />
    default:
      return <Navigate to="/login" replace />
  }
}

// Role-based route configuration
const roleRoutes = {
  mentor: [
    { path: 'reports', element: <MentorReport /> },
    { path: 'statistics', element: <DataStatistics /> },
    { path: 'students', element: <MonitorStudents /> },
  ],
  teacher: [
    { path: 'evaluations', element: <FinalEvaluation /> },
    { path: 'progress', element: <MonitorStudentProgress /> },
    { path: 'mentor-evaluations', element: <MonitorMentorEvaluation /> },
  ],
  student: [
    { path: 'reports', element: <SubmitReport /> },
    { path: 'internship', element: <RegisterInternship /> },
    { path: 'profile', element: <EditProfile /> },
  ],
  admin: [
    // Admin specific routes
  ],
}

const App = () => {
  return (
    <Router>
      <div className="min-h-screen bg-background font-sans antialiased">
        <Toaster position="top-right" />
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />

          {/* Protected Dashboard Routes */}
          <Route
            path="/dashboard"
            element={
              <ProtectedRoute>
                <DashboardLayout>
                  <DashboardSelector />
                </DashboardLayout>
              </ProtectedRoute>
            }
          >
            {/* Nested routes will be rendered in the DashboardLayout */}
            <Route index element={<DashboardSelector />} />

            {/* Dynamic role-based routes */}
            {Object.entries(roleRoutes).map(([role, routes]) =>
              routes.map(({ path, element }) => (
                <Route
                  key={`${role}-${path}`}
                  path={path}
                  element={
                    <ProtectedRoute role={role}>
                      {element}
                    </ProtectedRoute>
                  }
                />
              ))
            )}
          </Route>

          {/* Catch all route */}
            } 
          />
        </Routes>
      </div>
    </Router>
  )
}

export default App
